import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import data from "../../data";

const Article = () => {
  const [article, setArticle] = useState();
  const { id } = useParams();

  useEffect(() => {
    for (const d of data) {
      if (d.id.tostring === id) {
        setArticle(d);
      }
    }
  });

  return (
    <div>
      {article && (
        <>
          <h1>{article.title}</h1>
        </>
      )}
    </div>
  );
};

export default Article;
